import './App.scss';
import React from 'react';
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link
} from "react-router-dom";
import Complete from './pages/Complete'
import Delete from './pages/Delete'
import Home from './pages/Home'


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      completed: [],
      deleted: []
    }
    this.createTask = this.createTask.bind(this)
    this.deleteTask = this.deleteTask.bind(this)
    this.editTask = this.editTask.bind(this)
    this.clearTask = this.clearTask.bind(this)
    // this.completeTask = this.completeTask(this)
  }
  createTask(name){
    const task = {
      id: Date.now(),
      name
    }
    this.setState({
      list: [...this.state.list, task]
    })
  }
  editTask(element){
    // console.log('edit');
  }
  // completeTask(element){
  //   this.setState((state, props) => {
  //     console.log(state);
  //       const completed = [...state.completed, element]
  //       const list = state.list.filter(item => item.id !== element.id)
  //       console.log("deleted", completed);
  //       console.log("list", list);


  //       return {
  //         completed,
  //         list
  //       }
  //     });

  // }
  clearTask(btn){
    const del = this.state.deleted.filter(item => item.id !== +btn);
    // const del = this.state.deleted.filter(item => console.log(item.id));

    // console.log(+btn);


    // item.id !== element.id
      // const completed = this.state.deleted.filter(item => item.id !== element.id)


    this.setState({
      deleted: del

      // 

      // if (btn === 'complete') {
      //   return (
      //     <Complete this/>


      //   )
      //     completed

      //   }

      // // } else {
      // //   return {
      // //     deleted

      // //   }

      // }

    })



  }
  deleteTask(element, btn){
    // console.log(element);
      this.setState((state, props) => {
      // console.log(this.state.deleted);
        const deleted = [...state.deleted, element]
        const completed = [...state.deleted, element]
        const list = state.list.filter(item => item.id !== element.id)
        // console.log("deleted", deleted);
        // console.log("list", list);
        if (btn === 'complete') {
          return {
            completed,
            list
          }

        } else {
          return {
            deleted,
            list
          }

        }
      });

  }

  render () {
    return (
      <div>
        <Router>
                <div className='Navigation'>
                    <div className="card text-center">
                        <div className="card-header">
                            <ul className="nav nav-pills card-header-pills">
                                <li className="nav-item" onClick={this.pathName}>
                                    <Link to={'/'}> Home </Link>
                                </li>
                                <li className="nav-item" onClick={this.pathName}>
                                    <Link to={'/complete'}> Complete </Link>
                                </li>
                                <li className="nav-item" onClick={this.pathName}>
                                    <Link to={'/delete'}> Delete </Link>
                                </li>
                            </ul>
                        </div>
                        <Routes>
                            <Route path='/' element={<Home 
                            createTask={this.createTask} 
                            delTask={this.deleteTask} 
                            compTask={this.completeTask} 
                            list={this.state.list}/>}/>
                            <Route path="/Complete" element={<Complete  list={this.state.completed} clearTask={this.clearTask}/>} />
                            <Route path='/Delete' element={<Delete list={this.state.deleted} clearTask={this.clearTask}  />} />
                        </Routes>
                    </div>
                </div>
            </Router>
      </div>
    );
  }
}

export default App;
