import React, { Component } from 'react';
import ContentEditable from 'react-contenteditable'

class ListItem extends Component {
    constructor (props) {
        super(props);
        this.state = {
            list: []
        }
        this.contentEditable = React.createRef();
        this.completeBtn = this.completeBtn.bind(this)
        this.editBtn = this.editBtn.bind(this)
        this.deleteBtn = this.deleteBtn.bind(this)
        // this.listItemRef = React.createRef();

    }
    completeBtn(event){
        console.log(this.props.list);
        // this.props.compTask(this.props.item)


    }
    editBtn(event){
        console.log();
        // console.log(event.target.offsetParent);
        // event.target.offsetParent

        // this.props.editTask(this.props.item)

    }
    deleteBtn(event){
        // this.props.delTask(this.props.item)
        // console.log(event.target.id === 'complete')
        this.props.delTask(this.props.item, event.target.id)



    }
    render() {
    const { item } = this.props;

        return (
            <ContentEditable>
            <li className="list-group-item list-group-item-info"   id={item.id}>{ item.name } 
                <button id='complete' onClick={this.deleteBtn}>COMPLETE</button> 
                <button onClick={this.editBtn}>EDIT</button> 
                <button id='delete' onClick={this.deleteBtn}>DELETE</button> 

            </li>
            </ContentEditable>

        )
    }

}


export default ListItem
