import React, { Component } from 'react';

class ListItem extends Component {
    constructor (props) {
        super(props);
        this.state = {
            list: []
        }
        this.completeBtn = this.completeBtn.bind(this)
        this.editBtn = this.editBtn.bind(this)
        this.deleteBtn = this.deleteBtn.bind(this)
        this.listItemRef = React.createRef();

    }
    completeBtn(){
        // console.log(this.props.list);
        // const completeList = [...this.state.list, splice(this)]
        this.setState((state) => {
            return { list: state.list.splice(this)}
        })
        console.log(this.state.list);
        // list: [...this.state.list.splice(this)]

    }
    editBtn(){
        console.log('edit btn');
    }
    deleteBtn(event){
        this.props.delTask(this.listItemRef.current.id);

        // console.log(this.listItemRef.current.id);

    }
    render() {
    const { item } = this.props;

        return (
            <li className="list-group-item list-group-item-info" ref={this.listItemRef} id={item.id}>{ item.name } 
                <button onClick={this.completeBtn}>COMPLETE</button> 
                <button onClick={this.editBtn}>EDIT</button> 
                <button onClick={this.deleteBtn}>DELETE</button> 

            </li>

        )
    }

}


export default ListItem
