import React, { Component } from 'react'
// import ListItem from '../components/ListItem';




class Delete extends Component {
    constructor(props){
        super(props)
        this.state = {
            deleted: []
          }
        this.removeBtn = this.delElem.bind(this)

    }
    delElem(element){
        console.log(element);
        // const deleted = [...state.deleted, element]
        const delList = this.state.deleted.filter(item => item.id !== element.id)
        this.setState({

                deleted:  [...this.state.deleted, delLIst]



        })



    }
    render(){
        return (
            <div className="wrapper">
                <h1>Delete</h1>
                <ul className="list-group">
                {
                    this.props.list.map((item) => {
                        return (
                            <li className="list-group-item list-group-item-info"   key={item.id}>
                                <div className="itemLi">
                                    <div className="item">
                                        <span>{ item.name } </span>
                                    </div>
                                    <div className="buttons" id={item.id}>
                                        <button id='delete' onClick={this.delElem}>DELETE</button> 
                                    </div>
                                </div>
                            </li>
                        )
                        // return <ListItem key={item.id} item={item} removeBtn={this.removeBtn(this)}/>;

                    })  

                }

            </ul>

            </div>
        )
    }

}

export default Delete
