import React, { Component } from 'react';

class ListItem extends Component {
    constructor (props) {
        super(props);
        this.state = {
            list: [],
            editable: false,



        }
        this.contentEditable = React.createRef();
        this.editBtn = this.editBtn.bind(this)
        this.deleteBtn = this.deleteBtn.bind(this)

    }
    editBtn(event){
        const parent = event.target.closest(".itemLi")
        const addFocus = () => parent.querySelector('.item').focus()
        addFocus();
        this.setState( {
            editable: true 
        })
        console.log(event);
};


    deleteBtn(event){
        this.props.delTask(this.props.item, event.target.id)
    }
    render() {
    const { item } = this.props;

        return (

            <li className="list-group-item list-group-item-info"   id={item.id} >
                <div className="itemLi">
                    <div className="item" contentEditable={this.state.editable}   suppressContentEditableWarning={true}>
                            <span>{ item.name } </span>
                    </div>
                    <div className="buttons" id={item.id}>
                        <button id='complete' onClick={this.deleteBtn}>COMPLETE</button> 
                        <button onClick={this.editBtn}>EDIT</button> 
                        <button id='delete' onClick={this.deleteBtn}>DELETE</button> 
                    </div>
                </div>
            </li>
        )
    }
}


export default ListItem
