import './App.scss';
import React from 'react';
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link
} from "react-router-dom";
import Complete from './pages/Complete'
import Delete from './pages/Delete'
import Home from './pages/Home'


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      completed: [],
      deleted: []
    }
    this.createTask = this.createTask.bind(this)
    this.deleteTask = this.deleteTask.bind(this)
  }
  createTask(name){
    const task = {
      id: Date.now(),
      name
    }
    this.setState({
      list: [...this.state.list, task]
    })
    console.log(task);
  }
  deleteTask(elementId){

    const rest = this.state.list.filter(item => item.id !== +elementId)
    const element = this.state.list.filter(item => item.id === +elementId)


    this.setState({
      deleted: [...this.state.deleted, element[0]],
      list: rest

    })

    console.log(element);
  }


  render () {
    return (
      <div>
        <Router>
                <div className='Navigation'>
                    <div className="card text-center">
                        <div className="card-header">
                            <ul className="nav nav-pills card-header-pills">
                                <li className="nav-item" onClick={this.pathName}>
                                    <Link to={'/'}> Home </Link>
                                </li>
                                <li className="nav-item" onClick={this.pathName}>
                                    <Link to={'/complete'}> Complete </Link>
                                </li>
                                <li className="nav-item" onClick={this.pathName}>
                                    <Link to={'/delete'}> Delete </Link>
                                </li>
                            </ul>
                        </div>
                        <Routes>
                            <Route path='/' element={<Home createTask={this.createTask} delTask={this.deleteTask} list={this.state.list}/>}/>
                            <Route path="/Complete" element={<Complete list={this.state.completed}/>} />
                            <Route path='/Delete' element={<Delete list={this.state.deleted}/>} />
                        </Routes>
                    </div>
                </div>
            </Router>
      </div>
    );
  }
}

export default App;
